<?phpnamespace config;class connection{   private $hostName = 'localhost';   private $username = 'root';   private $password = '';   private $DbName = 'finalcapstone';   public function Connect()   {       $this->conn = new \mysqli($this->hostName,$this->username,$this->password,$this->DbName);       if ($this->conn->connect_error){           die('Connection Problem');       }      return $this->conn;   }}