<?phpnamespace controller;use models\category;include '../models/category.php';class categoryController{ public function store():void {     $categoryName = htmlspecialchars($_POST['categoryName']);     if (empty($categoryName)){         echo json_encode(['success' => false,'message' => 'category name is required']);         return;     }     $category = new category();     $category->store($categoryName); } public function show(): void {     $categoryList = new category();     $category = $categoryList->show();     $select = '';     if ($category){         foreach ($category  as $row){             $select .= '                <option value="'.$row['category_name'].'">'.$row['category_name'].'</option>             ';         }     }else{         $select .= '<option value="">No Category Available</option>';     }     echo $select; } public function showBaseOnId():void {     $catId = htmlspecialchars($_POST['catId']);     if (!isset($catId)){         echo json_encode(['success' => false, 'message' => 'No Id probated']);         return;     }     $category = new category();    $data = $category->showBaseOnId($catId);    $modal = '';    if ($data){        foreach ($data as $row){            $modal .= '            <!-- Modal -->                <div class="modal fade" id="modal_categoryUpdate_'.$catId.'" tabindex="-1" data-bs-backdrop="static" aria-labelledby="exampleModalLabel" aria-hidden="true">                    <form id="categoryFormUpdate" class="modal-dialog modal-dialog-centered">                        <div class="modal-content">                            <div style="background-color: #6610F2" class="modal-header">                                <h1 class="modal-title fs-6 text-light" id="exampleModalLabel">Update Category</h1>                                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>                            </div>                            <div class="modal-body">                                <div class="form-floating mb-3">                                    <input name="categoryName" value="'.$row['category_name'].'" type="text" class="form-control" id="categoryName">                                    <label for="categoryName">Category Name</label>                                    <div id="category_msg" class="invalid-feedback"></div>                                </div>                            </div>                            <input type="hidden" name="catId" value="'.$row['category_id'].'">                            <div class="modal-footer">                                <button  type="button" class="btn bg-danger text-light bg-opacity-75" data-bs-dismiss="modal">Close</button>                                <button style="background-color: #6610F2" type="submit" class="btn text-light">                                    Save Changes                                </button>                            </div>                        </div>                    </form>                </div>            ';            echo $modal;        }    } } public function showToCategoryLis():void {     $categoryList = new category();     $category = $categoryList->show();     $table = '';     if ($category){         foreach ($category  as $row){             $table .= '                <tr>                   <th>'.$row['category_id'].'</th>                   <td>'.$row['category_name'].'</td>                   <td>                   <button style="background-color: #6610F2" id="btnEditCategoy" value="'.$row['category_id'].'" class="btn badge text-light">Edit</button>                   <button  style="background-color: #6610F2"  id="btnArchive" value="'.$row['category_id'].'" class="btn badge text-light">Archive</button>                 </td>               </tr>             ';         }     }     echo $table; } public function update():void {     $categoryName = htmlspecialchars($_POST['categoryName']);     $catId = $_POST['catId'];     if (empty($categoryName)){        echo json_encode(['success' => false,'message' => 'Category Name is Required']);        return;     }     $category = new category();     $category->update($categoryName,$catId); } public function archive():void {     $catId = trim($_POST['cateId']);     if (!isset($catId)){         echo json_encode(['success' => false,'message' => 'No Id Probated']);         return;     }     $category = new category();     $category->archive($catId); }}if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['action'])){    $category = new categoryController();    switch ($_POST['action']){        case 'store':            $category->store();            break;        case 'showBaseOnId':            $category->showBaseOnId();            break;        case 'update':            $category->update();            break;        case 'archive':            $category->archive();            break;    }}