<?phpnamespace controller;include '../models/sales.php';use models\SalesModel;class salesController{    public function displayMonthlySales(): void    {        $salesModel = new SalesModel();        $salesData = $salesModel->GenerateMonthlySales();        echo json_encode($salesData); // Encode and output the salesController data as JSON    }    public function GenerateEveryDaySales():void    {        $salesModel = new SalesModel();       $salesData = $salesModel->GenerateEveryDaySales();        echo json_encode($salesData);    }    public function GetCountedSales():void      {        $sum = new SalesModel();        $total = $sum->GetTotalSales();        echo json_encode(number_format($total));    }}if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['action'])){    $controller = new salesController();    switch ($_POST['action']){        case 'GetMonthly':            $controller->displayMonthlySales();            break;        case 'GetDaily':            $controller->GenerateEveryDaySales();            break;        case 'totalSales':            $controller->GetCountedSales();            break;    }}