<?phpnamespace models;use config\connection;include '../config/connection.php';class auth extends connection{   public function loginUser(string $username,string $password,$role):void   {       $query = "SELECT * FROM users WHERE username = ? AND  role = ?";       $stmt = $this->Connect()->prepare($query);       $stmt->bind_param('ss',$username,$role);       $stmt->execute();       $count = $stmt->get_result();       if ($count->num_rows > 0){           $user = $count->fetch_assoc();           if (password_verify($password,$user['password'])){               session_start();               $_SESSION['username'] = $username;               $_SESSION['role'] = $role;               echo json_encode(['success' => true, 'message' => 'Successfully Logged, role: '.$role]);           }else{               echo json_encode(['success' => false, 'message' => 'Username or Password is Wrong']);           }       }else{           echo json_encode(['success' => false, 'message' => 'Account is not Found']);       }   }}