<?phpnamespace models;require_once '../config/connection.php';use config\connection;class category extends connection{  public function store($category):void  {      $query = "INSERT INTO category(category_name)VALUES (?)";      $stmt = $this->Connect()->prepare($query);      $stmt->bind_param('s',$category);      if (!$stmt->execute()){          echo json_encode(['success' => false , 'message' => "Error to Perform prepared statement"]);          return;      }      echo json_encode(['success' => true, 'message' => 'Successfully Added New Category']);  }  public function show():array  {      $result = $this->Connect()->query("Select * FROM category WHERE status = 'Active'");      $dataRow = [];      if ($result->num_rows > 0){          while ($row = $result->fetch_assoc()){            $dataRow[] = $row;          }          return $dataRow;      }      return [];  }  public function showBaseOnId(int $catId):array  {      $query = "SELECT * FROM category WHERE category_id = ?";      $stmt = $this->Connect()->prepare($query);      $stmt->bind_param('i',$catId);      $stmt->execute();      $result = $stmt->get_result();      $dataRow = [];      if ($result->num_rows > 0){          while ($row = $result->fetch_assoc()){              $dataRow[] = $row;          }          return $dataRow;      }      return [];  }  public function update(string $categoryName ,int $catId): void  {      $query = "UPDATE category SET category_name = ? WHERE category_id = ?";      $stmt = $this->Connect()->prepare($query);      $stmt->bind_param('si',$categoryName,$catId);      if ($stmt->execute()){          echo json_encode(['success' => true, 'message' => 'Successfully Update']);      }else{        echo json_encode(['success' => false, 'message' => 'Error'.$stmt->error]);      }      $stmt->close();  }  public function archive(int $catId):void  {      $status = 'IsDeleted';      $query = "UPDATE category SET status = ? WHERE category_id = ?";      $stmt = $this->Connect()->prepare($query);      $stmt->bind_param('si',$status,$catId);      if ($stmt->execute()){          echo json_encode(['success' => true, 'message' => 'Successfully Deleted']);      }else{          echo json_encode(['success' => false, 'message' => 'Error'.$stmt->error]);      }      $stmt->close();  }}