<?phpnamespace models;require_once '../config/connection.php';use config\connection;class SalesModel extends connection{    public function GenerateMonthlySales(): array    {        $result = $this->Connect()->query("           SELECT                months.month AS sale_month,               COALESCE(SUM(s.total_price), 0) AS total_sales           FROM               (SELECT 1 AS month UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL                SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL                SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 UNION ALL                SELECT 10 UNION ALL SELECT 11 UNION ALL SELECT 12) AS months           LEFT JOIN               sales s ON months.month = MONTH(s.sales_created)                   AND YEAR(s.sales_created) = YEAR(CURDATE())           GROUP BY                months.month           ORDER BY                months.month;       ");        $data = [];        while ($row = $result->fetch_assoc()) {            $data[] = $row;        }        return $data; // Return salesController data as an array    }    public function GenerateEveryDaySales(): array    {        $result = $this->Connect()->query("        SELECT            DAYOFWEEK(s.sales_created) AS sale_day,            COALESCE(SUM(s.total_price), 0) AS total_sales        FROM            sales s        WHERE            MONTH(s.sales_created) = MONTH(CURDATE())            AND YEAR(s.sales_created) = YEAR(CURDATE())        GROUP BY            DAYOFWEEK(s.sales_created)        ORDER BY            sale_day;    ");        $data = [];        while ($row = $result->fetch_assoc()) {            $data[] = $row;        }        return $data;    }    public function GetTotalSales():int    {        $result = $this->Connect()->query("SELECT SUM(total_price) AS total FROM sales");        $data = $result->fetch_assoc();        return (int) $data['total'];    }}