<?phpnamespace models;use config\connection;require '../config/connection.php';class salesReport extends connection{   public function GenerateSalesReport(string $start_date,string $end_date):array   {       $query = "       SELECT product_Id as productId, SUM(quantity_sold) AS QuantitySold, SUM(total_price) AS SalesAmount        FROM sales        WHERE sales_created BETWEEN ? AND ?        GROUP BY product_Id;       ";         $stmt = $this->Connect()->prepare($query);            $stmt->bind_param('ss',$start_date,$end_date);            $stmt->execute();            $result = $stmt->get_result();            if ($result->num_rows > 0){                $dataRow = [];                while ($row = $result->fetch_assoc()){                    $dataRow[] = $row;                }                return $dataRow;            }            return [];   }}