tems-center">                <form id="formFilter" class="d-lg-flex gap-3">                    <div>                        <label for="start_date">                            <input type="date" class="form-control" id="start_date" name="start_date">                        </label>                    </div>                    <div>                      <label for="end_date">                          <input type="date" class="form-control" id="end_date" name="end_date">                      </label>                    </div>                    <div>                        <button style="background-color: #1c3f7c" type="submit" class="btn text-light" id="filter">Filter</button>                    </div>                </form>                <button style="background-color: #1c3f7c" id="btn_pdfDownload" class="btn text-light">                    Download Report                </button>            </div>        <table id="myTable" class="table table-hover table-hover table-striped">            <thead style="background-color: #1c3f7c;" class="text-light" >            <tr>                <th>ProductName</th>                <th>Quantity Sold</th>                <th>Sales Amount</th>            </tr>            </thead>            <tbody id="ReportData">            </tbody>        </table>    </div></div></body></html><script>    new DataTable('#myTable', {        paging: true,        responsive: true    } );</script><script>$(document).ready(function (){    $(document).on('submit','#formFilter',function (event){        event.preventDefault()        var formData = new FormData(this)        formData.append('action','filter')        $.ajax({            url: '../controllers/salesReportController.php',            type: 'post',            data: formData,            contentType: false,            processData: false,            success: function (res){                $('#ReportData').html(res)            }        })    })    $(document).on('click', '#btn_pdfDownload', function () {        var start_date = $('#start_date').val();        var end_date = $('#end_date').val();        if (start_date === '' || end_date === ''){            alert('Please select a date range')            return        }        window.open('../controllers/salesReportController.php?start_date=' + start_date + '&end_date=' + end_date, '_blank');    });})</script>